    <script>
        jQuery(document).ready(function($) {
            // var exchangeRate = 20.0858; // 日本円から香港ドルの為替レート
            let exchangeRate = 0.008755

            $(".content--price").each(function() {
                var priceText = $(this).text().replace(/[^\d]/g, ""); // 数値のみ取得
                var priceJPY = parseFloat(priceText);

                if (!isNaN(priceJPY)) {
                    var priceHKD = (priceJPY * exchangeRate).toFixed(2); // 小数点2桁
                    $(this).text("S$ " + priceHKD.toLocaleString()); // 変換後のテキストを適用
                }
            });
            $(".renew_2024_result_item_price").each(function() {
                var priceText = $(this).text().replace(/[^\d]/g, ""); // 数値のみ取得
                var priceJPY = parseFloat(priceText);

                if (!isNaN(priceJPY)) {
                    var priceHKD = (priceJPY * exchangeRate).toFixed(2); // 小数点2桁
                    $(this).text("S$ " + priceHKD.toLocaleString()); // 変換後のテキストを適用
